drop table posters;

create table posters(
	id serial not null,
	tmdb int,
	imdb text,
	titulo_original text ,
	titulo_traduzido text ,
	ano int,
	pagina int,
	pasta int,
	data_release text,
	link_imagem text,
	sinopse text,
	cores text
); 

SELECT ID,
	TMDB,
	IMDB,
	TITULO_ORIGINAL,
	TITULO_TRADUZIDO,
	ANO,
	PAGINA,
	PASTA,
	DATA_RELEASE,
	LINK_IMAGEM,
	SINOPSE,
	CORES
FROM
	POSTER
ORDER BY
	ID DESC;



