INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3893, 3078, 'tt0025316', 'IT HAPPENED ONE NIGHT', 'ACONTECEU NAQUELA NOITE', 1934, 1, 1, '1934-02-22', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/2PNUGWAflH6UUumas0POMmokHlc.jpg', 'Peter Warren, um jornalista desempregado, encontra Ellie, a filha de um milionário que fugiu do iate de Alexander Andrews, seu pai, pois este não aprova quem ela escolheu como marido. Peter vê a oportunidade de obter uma boa matéria, mas vários fatos criam uma forte aproximação entre eles. -         ', 'Preto Branco');
