INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3891, 157898, 'tt0025046', 'DESIRABLE', 'DESIRABLE', 1934, -1, -1, '1934-09-08', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/7WQ9G4QCWPVzGcAL9p02whkVtbv.jpg', '', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3890, 43867, 'tt0029442', 'THE PRISONER OF ZENDA', 'O PRISIONEIRO DE ZENDA', 1937, -1, -1, '1937-09-03', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/tvMF7iljOgOXLvkVsWGr5GrPE4j.jpg', 'Durante uma viagem de férias a um reino europeu, um inglês descobre, para seu espanto, que é um sósia perfeito do rei do lugar. Quando o rei verdadeiro é impedido de coroar, o forasteiro é forçado a se passar por ele e assumir a coroa temporariamente. Porém uma reviravolta ocorre quando ele se apaixona pela nova rainha e o rei verdadeiro é sequestrado pelo seu meio-irmão. A partir daí, a vida do rei (o prisioneiro de Zenda) e o futuro dos súditos está em suas mãos. Poderá ele resgatar o verdadeiro rei e ainda continuar vivo?', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3889, 408, 'tt0029583', 'SNOW WHITE AND THE SEVEN DWARFS', 'BRANCA DE NEVE E OS SETE ANÕES', 1937, 1, 1, '1937-12-08', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/9QKUh9UuIIT2ZA9Rby9R6vtNRKa.jpg', 'A rainha malvada morre de ciúmes da beleza de Branca de Neve e manda mata-la. Logo, descobre que a jovem não morreu e está morando na floresta com sete amiguinhos. A princesa então é envenenada pela rainha e só o beijo de um príncipe pode salvá-la.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3888, 119645, 'tt0029842', 'THE ADVENTURES OF MARCO POLO', 'AS AVENTURAS DE MARCO POLO', 1938, 1, 1, '1938-04-07', 'https://www.themoviedb.org/t/p/w600_and_h900_bestv2/iXZoxhfhJGByz1zP72PtELPhrLs.jpg', 'Marco Polo cruza o mar em busca de tesouros e aventuras. Com a ajuda de seu fiel e covarde amigo Binguccio, encontra na China, como primeiro visitante do país europeu, várias inovações práticas como massas e explosivos. Ele é também apresentado a Kublai Khan, o imperador sábio e benevolente da China, e sua adorável filha, a Princesa Kukachin. O romance começa a florescer entre Marco e a princesa, mas Ahmed, o dissimulado assistente do Imperador, também tem seus olhos sobre a princesa e ele está determinado a conquistar a mão dela e assim usurpar o trono de Kublai Khan.', 'preto_branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3887, 99865, 'tt0030384', 'LORD JEFF', 'PEQUENO PETULANTE', 1938, -1, -1, '1938-06-17', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/x2EYZ88Q6y4qzv20lu4FfNDrApN.jpg', '', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3886, 213064, 'tt0030679', 'RICH MAN, POOR GIRL', 'MUITO CUSTA CASAR', 1938, -1, -1, '1938-08-12', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/fCqkjofCXOuqggEmlSBartryQb4.jpg', 'O jovem e rico executivo Bill Harrison se apaixona por Joan Thayer, sua secretária. Mas ela não acredita que ele será capaz de aceitar seus parentes. Decidido a se casar com Joana, Bill vai morar com sua família para provar o amor que sente por ela. Durante sua estada, Bill aprenderá mais sobre o estilo de vida de pessoas de classe média baixa.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3885, 34106, 'tt0030993', 'YOU CAN''T TAKE IT WITH YOU', 'DO MUNDO NADA SE LEVA', 1938, 1, 1, '1938-09-01', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/9JltqbDyWstYW6gnvqDO6L5bOrx.jpg', 'Alice Sycamore (Jean Arthur) deseja apresentar Tony Kirby (James Stewart), seu noivo, à sua família, mas como ele provêm de uma família rica e influente e a sua é composta de pessoas extrovertidas e um tanto lunáticas, há um choque de comportamentos. Para completar, Anthony P. Kirby (Edward Arnold), o pai do seu noivo, já comprou todos os imóveis da região onde a família de Alice vive para um importante empreendimento, com exceção da casa de Alice. Sem este imóvel o projeto não pode ir adiante e, como há uma recusa em vender, este posicionamento gera um clima desfavorável entre as duas famílias.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3884, 13696, 'tt0029870', 'ANGELS WITH DIRTY FACES', 'ANJOS DE CARA SUJA', 1938, 1, 1, '1938-11-26', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/k23E4UAcow8eczLRmVCMdukL4Mx.jpg', 'Rocky (James Cagney) e Jerry (Pat O''Brien) são dois amigos de infância que viviam em um bairro barra-pesada de Nova York. Depois de crescidos, eles tomam rumos bem diferentes: Rocky se torna um famoso gângster e Jerry um padre engajado. As crianças da vizinhança idolatram o criminoso, mas perdem um pouco de respeito depois que ele cede aos pedidos de Jerry para não fazer uma execução. O caminho dos dois vai se cruzar, assim como seus interesses diversos.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3883, 43829, 'tt0031507', 'JESSE JAMES', 'JESSE JAMES', 1939, -1, -1, '1939-01-14', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/s85NggId22ntp4pbYwTbtHdalnY.jpg', 'Jesse James e seu irmão Frank juram vingar a morte da mãe, morta por um representante da companhia ferroviária de Saint Louis. Eles começam a carreira criminosa roubando os passageiros dos trens, mas logo expandem suas atividades cometendo assalto a bancos.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3882, 776, 'tt0031885', 'LA RÈGLE DU JEU', 'A REGRA DO JOGO', 1939, 1, 1, '1939-07-09', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/tmtossMIfsjf1eLCUnmTKsVopGU.jpg', 'O aviador André Jurieux bateu recordes de vôo, mas só consegue pensar em sua amada Christine, mulher do aristocrata Robert de la Cheyniest. Jurieux consegue com um amigo um convite para a casa de campo em que o casal está dando uma grande festa de caça. Os sorrisos cordiais dos convidados escondem, porém, segredos e sentimentos, e o resultado disso é um assassinato.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3881, 770, 'tt0031381', 'GONE WITH THE WIND', '...E O VENTO LEVOU', 1939, 1, 1, '1939-12-15', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/1FdHtwie0iy6PWXgn2Gr6Zh83HU.jpg', '"E o Vento Levou" narra a complicada vida de Scarlet O’Hara, seus amores e desilusões em um período que tem a Guerra Civil Americana como pano de fundo. Clark Gable é Rett Butler, um vivido aventureiro que passa pela vida de Scartlet, em uma relação de amor e ódio marcada por conflitos já clássicos e inesquecíveis cenas de amor.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3880, 10895, 'tt0032910', 'PINOCCHIO', 'PINÓQUIO', 1940, 2, 1, '1940-02-23', 'https://www.themoviedb.org/t/p/w600_and_h900_bestv2/bnZJrLRnoQHpzEJdka1KYfsAF3N.jpg', 'Gepeto é um carpinteiro solitário que, um dia, resolve fazer um boneco de madeira para lhe fazer companhia. Durante a noite, a Fada Azul dá vida ao boneco, que passa a se chamar Pinóquio. Ansioso para se tornar um menino de verdade, Pinóquio se mete em várias confusões, apesar dos constantes avisos de seu amigo Grilo Falante. O boneco tem uma particularidade: sempre que mente seu nariz cresce. Até o dia em que precisa resgatar seu criador, quando ele fica preso na barriga de uma baleia. -         ', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3879, 756, 'tt0032455', 'FANTASIA', 'FANTASIA - 50 ANOS', 1940, 91, -1, '1940-04-11', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/lCfKb2nRduQpUk1aYaeSp05MMQv.jpg', 'A integração das grandes obras da música clássica com visuais extremamente criativos e originais da animação. São as seguintes composições que fazem parte do filme: "Toccata e Fuga em Ré Menor", de Johann Sebastian Bach; "Suíte Quebra-Nozes", de Peter Llich Tchaikovsky; "O Aprendiz de Feiticeiro", de Paul Dukas; "Sagração da Primavera", de Igor Stravinsky; "Sinfonia Pastoral", de Ludwing Van Beethoven; "Dança das Horas", de Almicare Ponchielli; "Noite no Monte Calvo", de Modest Mussorgsky; e "Ave Maria", de Franz Schubert.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3878, 756, 'tt0032455', 'FANTASIA', 'FANTASIA', 1940, 2, 1, '1940-04-11', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/aaJ2OMxYsAmtpHMTCy4pMliM17T.jpg', 'A integração das grandes obras da música clássica com visuais extremamente criativos e originais da animação. São as seguintes composições que fazem parte do filme: "Toccata e Fuga em Ré Menor", de Johann Sebastian Bach; "Suíte Quebra-Nozes", de Peter Llich Tchaikovsky; "O Aprendiz de Feiticeiro", de Paul Dukas; "Sagração da Primavera", de Igor Stravinsky; "Sinfonia Pastoral", de Ludwing Van Beethoven; "Dança das Horas", de Almicare Ponchielli; "Noite no Monte Calvo", de Modest Mussorgsky; e "Ave Maria", de Franz Schubert.', 'Preto Branco');
