INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3876, 252701, 'tt0032959', 'QUEEN OF THE YUKON', 'GIGANTES DA FLORESTA', 1940, -1, -1, '1940-08-26', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/aX4bI9TZEdMGmbxrXl3jnjie7KF.jpg', 'Sadie é dona de um barco que garimpeiros estão usando para viajar para suas reivindicações, enquanto jogam e bebem  no percurso.', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3875, 217027, 'tt0032503', 'GALLANT SONS', 'FILHOS DE BRIO', 1940, -1, -1, '1940-11-15', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/ryWxQ6wpTX4UCwwErtY8teQX4rb.jpg', '', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3874, 15, 'tt0033467', 'CITIZEN KANE', 'CIDADÃO KANE', 1941, 2, 1, '1941-04-17', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/sav0jxhqiH0bPr2vZFU0Kjt2nZL.jpg', 'Charles Foster Kane foi um menino pobre que acaba se tornando um dos homens mais ricos do mundo. O filme inicia com a sua morte, momentos antes da qual pronuncia a palavra "Rosebud". Após dias de sensacionalismo em cima da notícia de sua morte, o jornalista Jerry Thompson é enviado por seu chefe para investigar a vida de Kane, a fim de descobrir o sentido de sua última palavra, a qual ninguém sabia. Entrevistando pessoas do passado de Kane, o jornalista mergulha na vida de um homem solitário, que desde a infância é obrigado a seguir a vontade alheia. Ninguém a seu redor importa-se com Kane, que busca por meio da aquisição de bens a adoração das pessoas. Ao final, Thompson, após a exaustiva investigação da vida de Kane através de entrevistas, se vê incapaz de descobrir o significado da palavra, concluindo que "Charles Foster Kane foi um homem que possuiu tudo o que quis e depois perdeu tudo. Talvez Rosebud seja algo que ele nunca tenha possuído ou algo que tenha perdido".', 'Preto Branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(3873, 54635, 'tt0034386', 'WHEN LADIES MEET', 'DE MULHER PARA MULHER', 1941, -1, -1, '1941-08-29', 'https://image.tmdb.org/t/p/w600_and_h900_bestv2/b8JFVbtijLnVkaf9rTP0ebjnCHc.jpg', '', 'Preto Branco');
