INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(114, 42604, 'tt0064381', 'GOODBYE, COLUMBUS', 'PAIXÃO DE PRIMAVERA', 1969, -1, -1, '1969-04-03', 'https://www.themoviedb.org/t/p/w600_and_h900_bestv2/sbL9RzE4Udc7HFYWvKDPKedAqkT.jpg', 'Um graduado inteligente e veterano do Exército da classe trabalhadora tem um caso e entra em conflito com uma jovem "novo-rica" ​​que não se importa com métodos anticoncepcionais ou com o uso de quaisquer outras precauções para evitar a gravidez', 'preto_branco');
INSERT INTO poster (id, tmdb, imdb, titulo_original, titulo_traduzido, ano, pagina, pasta, data_release, link_imagem, sinopse, cores) VALUES(113, 297158, 'tt0028388', 'TO MARY - WITH LOVE', 'ESPOSO E AMANTE', 1936, -1, -1, '1936-08-01', 'https://www.themoviedb.org/t/p/w600_and_h900_bestv2/5arQE7wQgnlkihMH8PiHYYHu5rG.jpg', 'Dez anos de vida de casado, começando em 1925. Mary apoia Jack após a Depressão de 1929, mas considera o divórcio quando ele se torna bem-sucedido novamente em 1935. Bill, que ama Mary, trabalha para mantê-los juntos.', 'preto_branco');
